import React, { useState } from "react";
import concerts from "./concerts"; // Import your concerts data
import "./App.css";

function BuyProducts() {
    const [cart, setCart] = useState([]);

    // Handle adding items to the cart
    const addToCart = (product, quantity) => {
        if (quantity > 0) {
            setCart((prevCart) => [
                ...prevCart,
                { ...product, quantity: quantity },
            ]);
            alert(`${quantity} ${product.type}(s) added to the cart!`);
        } else {
            alert("Quantity must be at least 1.");
        }
    };

    return (
        <div className="BuyProducts">
            <h1>End of the Year Sale</h1>
            {concerts.map((concert) => (
                <div key={concert.month}>
                    <h2>
                        {concert.month}: {concert.venue}, {concert.artist}
                    </h2>
                    <div style={{ display: "flex", gap: "20px", flexWrap: "wrap" }}>
                        {concert.products.map((product) => {
                            const [quantity, setQuantity] = useState(1); // State for product quantity

                            return (
                                <div
                                    key={product.id}
                                    style={{
                                        border: "1px solid #ddd",
                                        padding: "10px",
                                        textAlign: "center",
                                    }}
                                >
                                    <img
                                        src={product.image}
                                        alt={product.type}
                                        style={{ width: "150px", height: "150px", objectFit: "cover" }}
                                    />
                                    <p>{product.type}</p>
                                    <p>Price: ${product.price}</p>
                                    <div style={{ display: "flex", justifyContent: "center", alignItems: "center", gap: "10px" }}>
                                        <button onClick={() => setQuantity((q) => Math.max(1, q - 1))}>-</button>
                                        <span>{quantity}</span>
                                        <button onClick={() => setQuantity((q) => q + 1)}>+</button>
                                    </div>
                                    <button
                                        onClick={() => addToCart(product, quantity)}
                                        style={{ marginTop: "10px", padding: "5px 10px", background: "black", color: "white" }}
                                    >
                                        Add to Cart
                                    </button>
                                </div>
                            );
                        })}
                    </div>
                </div>
            ))}
        </div>
    );
}

export default BuyProducts;
